mapper.select:

	select rno, qno, reply, replyer, replydate, updatedate
    from
	   (
		select /*+ INDEX(tbl_reply idx_reply) */
		rownum rn, rno, qno, reply, replyer, replyDate, updatedate
	from qa_reply
	where qno = #{qno}
		and rno > 0
		and rownum <= #{cri.pageNum} * #{cri.amount}
	) where rn > (#{cri.pageNum} -1) * #{cri.amount}
